package basic;

public class DataTypeExample {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//String - class/data type , name - variable - aayesha - value
		String name="Aayesha";
		//int - data type , age - variable 20 - value
		
		int age=20;
		long mobile_no=9090909090L;
		float percentage=89.56f;
		//String address="A-102 test Road,C G Road , Ahmedababd-5459405";
		String address="A-102 test Road,C G Road , Ahmedababd-5459405";
		char grade='A';
		System.out.println("Name "+name);
		System.out.println("Mobile Number "+mobile_no);
		System.out.println("Grade "+grade);
		System.out.println(name+"\t"+age+"\t"+mobile_no+"\t"+percentage+"\t"+address+"\t"+grade);
		
		
		
	}

}
