package basic;

public class WhileExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//print even values from 1 to 100
		int i=2;
		while(i<=100)
		{
			//System.out.println(i);
			System.out.println("i = "+i);
			i=i+2;
		}	
		

	}

}
